package ru.kpfu.itis.lzakharov.contacts.service.impl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ru.kpfu.itis.lzakharov.contacts.model.Client;import ru.kpfu.itis.lzakharov.contacts.model.Record;import ru.kpfu.itis.lzakharov.contacts.repository.ClientRepository;import ru.kpfu.itis.lzakharov.contacts.repository.RecordRepository;import ru.kpfu.itis.lzakharov.contacts.service.RecordService;import java.util.List;@Servicepublic class RecordServiceImpl implements RecordService {    @Autowired    ClientRepository clientRepository;    @Autowired    RecordRepository recordRepository;    @Override    public List<Record> find(String username) {        Client client = clientRepository.findByUsername(username);        return recordRepository.findByClient(client);    }    @Override    public Record create(Record record) {        return recordRepository.save(record);    }}